                   points_selector=models.FilterSelector(
                        filter=qmodels.Filter(
                            must=[
                            models.FieldCondition(
                            key="metadata.subgroup",
                            match=qmodels.MatchValue(value=subgroup)
                            ),
                            models.FieldCondition(
                            key="metadata.filepath",
                            match=qmodels.MatchValue(value=filepath)
